import React, { useState, useEffect } from 'react';
import { studyAPI } from '../services/api';
import '../styles/TaskList.css';

const TaskList = ({ refreshTrigger }) => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingTask, setEditingTask] = useState(null);

  // Fetch all tasks
  const fetchTasks = async () => {
    try {
      const response = await studyAPI.getAllTasks();
      setTasks(response.data);
    } catch (error) {
      alert('Error fetching tasks: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Load tasks on component mount and when refreshTrigger changes
  useEffect(() => {
    fetchTasks();
  }, [refreshTrigger]);

  // Delete task
  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        await studyAPI.deleteTask(id);
        fetchTasks(); // Refresh list
        alert('Task deleted successfully!');
      } catch (error) {
        alert('Error deleting task: ' + error.message);
      }
    }
  };

  // Toggle task status
  const toggleStatus = async (task) => {
    const newStatus = task.status === 'Pending' ? 'Completed' : 'Pending';
    try {
      await studyAPI.updateTask(task._id, { ...task, status: newStatus });
      fetchTasks(); // Refresh list
    } catch (error) {
      alert('Error updating task: ' + error.message);
    }
  };

  // Update task
  const handleUpdate = async (updatedTask) => {
    try {
      await studyAPI.updateTask(updatedTask._id, updatedTask);
      setEditingTask(null);
      fetchTasks(); // Refresh list
      alert('Task updated successfully!');
    } catch (error) {
      alert('Error updating task: ' + error.message);
    }
  };

  if (loading) {
    return <div>Loading tasks...</div>;
  }

  return (
    <div>
      <h2 className="task-list-title">Study Tasks ({tasks.length})</h2>
      {tasks.length === 0 ? (
        <p className="no-tasks">No tasks found. Add your first study task!</p>
      ) : (
        <div>
          {tasks.map((task) => (
            <div
              key={task._id}
              className={`task-card ${task.status === 'Completed' ? 'completed' : ''}`}
            >
              {editingTask === task._id ? (
                <EditTaskForm task={task} onSave={handleUpdate} onCancel={() => setEditingTask(null)} />
              ) : (
                <TaskDisplay
                  task={task}
                  onEdit={() => setEditingTask(task._id)}
                  onDelete={() => handleDelete(task._id)}
                  onToggleStatus={() => toggleStatus(task)}
                />
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// Task display component
const TaskDisplay = ({ task, onEdit, onDelete, onToggleStatus }) => {
  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'High': return '#ff4444';
      case 'Medium': return '#ffaa00';
      case 'Low': return '#00aa00';
      default: return '#666';
    }
  };

  return (
    <>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
        <h3 style={{ margin: 0 }}>{task.subjectName}</h3>
        <span
          style={{
            padding: '4px 8px',
            borderRadius: '4px',
            fontSize: '12px',
            fontWeight: 'bold',
            color: 'white',
            backgroundColor: getPriorityColor(task.priority)
          }}
        >
          {task.priority}
        </span>
      </div>

      <p><strong>Topic:</strong> {task.topic}</p>
      <p><strong>Date:</strong> {new Date(task.studyDate).toLocaleDateString()}</p>
      <p><strong>Time:</strong> {task.startTime} - {task.endTime}</p>
      <p>
        <strong>Status:</strong>
        <span style={{ color: task.status === 'Completed' ? 'green' : 'orange', fontWeight: 'bold' }}>
          {task.status}
        </span>
      </p>

      <div style={{ marginTop: '15px' }}>
        <button
          onClick={onToggleStatus}
          style={{
            backgroundColor: task.status === 'Pending' ? '#28a745' : '#ffc107',
            color: 'white',
            padding: '5px 10px',
            border: 'none',
            borderRadius: '3px',
            marginRight: '10px',
            cursor: 'pointer'
          }}
        >
          Mark as {task.status === 'Pending' ? 'Completed' : 'Pending'}
        </button>

        <button
          onClick={onEdit}
          style={{
            backgroundColor: '#007bff',
            color: 'white',
            padding: '5px 10px',
            border: 'none',
            borderRadius: '3px',
            marginRight: '10px',
            cursor: 'pointer'
          }}
        >
          Edit
        </button>

        <button
          onClick={onDelete}
          style={{
            backgroundColor: '#dc3545',
            color: 'white',
            padding: '5px 10px',
            border: 'none',
            borderRadius: '3px',
            cursor: 'pointer'
          }}
        >
          Delete
        </button>
      </div>
    </>
  );
};

// Edit task form component
const EditTaskForm = ({ task, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    ...task,
    studyDate: new Date(task.studyDate).toISOString().split('T')[0]
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div style={{ marginBottom: '10px' }}>
        <input
          type="text"
          name="subjectName"
          value={formData.subjectName}
          onChange={handleChange}
          placeholder="Subject Name"
          style={{ width: '100%', padding: '5px' }}
        />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <input
          type="text"
          name="topic"
          value={formData.topic}
          onChange={handleChange}
          placeholder="Topic"
          style={{ width: '100%', padding: '5px' }}
        />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <input
          type="date"
          name="studyDate"
          value={formData.studyDate}
          onChange={handleChange}
          style={{ width: '100%', padding: '5px' }}
        />
      </div>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
        <input
          type="time"
          name="startTime"
          value={formData.startTime}
          onChange={handleChange}
          style={{ flex: 1, padding: '5px' }}
        />
        <input
          type="time"
          name="endTime"
          value={formData.endTime}
          onChange={handleChange}
          style={{ flex: 1, padding: '5px' }}
        />
      </div>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
        <select
          name="priority"
          value={formData.priority}
          onChange={handleChange}
          style={{ flex: 1, padding: '5px' }}
        >
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>
        <select
          name="status"
          value={formData.status}
          onChange={handleChange}
          style={{ flex: 1, padding: '5px' }}
        >
          <option value="Pending">Pending</option>
          <option value="Completed">Completed</option>
        </select>
      </div>
      <div>
        <button type="submit" style={{ marginRight: '10px', padding: '5px 15px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '3px' }}>
          Save
        </button>
        <button type="button" onClick={onCancel} style={{ padding: '5px 15px', backgroundColor: '#6c757d', color: 'white', border: 'none', borderRadius: '3px' }}>
          Cancel
        </button>
      </div>
    </form>
  );
};

export default TaskList;
