import React, { useState } from 'react';
import AddTask from './components/AddTask';
import TaskList from './components/TaskList';

function App() {
  // State to trigger task list refresh
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  // Function to refresh task list after adding new task
  const handleTaskAdded = () => {
    setRefreshTrigger(prev => prev + 1);
  };

  return (
    <div style={{ 
      maxWidth: '800px', 
      margin: '0 auto', 
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    }}>
      {/* Header */}
      <header style={{ 
        textAlign: 'center', 
        marginBottom: '30px',
        borderBottom: '2px solid #007bff',
        paddingBottom: '20px'
      }}>
        <h1 style={{ 
          color: '#007bff',
          margin: 0,
          fontSize: '2.5rem'
        }}>
          ðŸ“š Study Planner
        </h1>
        <p style={{ 
          color: '#666',
          margin: '10px 0 0 0'
        }}>
          Organize your study schedule efficiently
        </p>
      </header>

      {/* Main Content */}
      <main>
        {/* Add Task Form */}
        <AddTask onTaskAdded={handleTaskAdded} />

        {/* Task List */}
        <TaskList refreshTrigger={refreshTrigger} />
      </main>

      {/* Footer */}
      <footer style={{ 
        textAlign: 'center', 
        marginTop: '40px',
        padding: '20px',
        borderTop: '1px solid #ddd',
        color: '#666'
      }}>
        <p>Study Planner - MERN Stack Project</p>
      </footer>
    </div>
  );
}

export default App;
